import React, { useRef, useEffect } from "react";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";
import "./SignUp.scss";
import axios from "axios";

const SignUp = (props) => {
  const cryptedRoleId = props.match.params.id;
  let roleId = "";

  switch (cryptedRoleId) {
    case "DFJLMdk123CDLEcjks":
      roleId = 1;
      break;
    case "dfqlQIF":
      roleId = 2;
      break;
    case "edjDFKJ":
      roleId = 3;
      break;
  }

  const { register, handleSubmit, reset, errors, watch } = useForm();
  const password = useRef({});
  password.current = watch("password", "");

  const onSubmit = (data) => {
    console.log(data);
    //run validation
    axios
      .post("/auth/signup", data)
      .then((response) => console.log(response.data));
    reset();
    //redirect to role-based view
  };

  const now = new Date();

  const now_formated = `${now.getFullYear()}-${
    now.getMonth() + 1
  }-${now.getDay()}`;

  useEffect(() => {
    console.log(now_formated);
  });

  return (
    <div className="signUp-page-container">
      <div className="signUp-container">
        <div className="signUp-img" />
        <div className="signUp-right-side-container">
          <h1>Create an Account</h1>
          <form onSubmit={handleSubmit(onSubmit)}>
            <div className="name">
              <input
                name="firstname"
                type="text"
                placeholder="First Name"
                ref={register({
                  required: "First Name is required",
                })}
              />
              <input
                name="lastname"
                type="text"
                placeholder="Last Name"
                ref={register({ required: "Last Name is required" })}
              />
            </div>
            <div className="comp-country">
              <input
                name="company"
                type="text"
                placeholder="Company"
                ref={register}
              />
              <input
                name="country"
                type="text"
                placeholder="Country"
                ref={register({ required: "Country is required" })}
              />
            </div>
            <input
              id="email"
              name="email"
              type="text"
              placeholder="Email Address"
              ref={register({ required: "Email is required" })}
            />
            <div className="password">
              <input
                name="password"
                type="password"
                placeholder="Password"
                ref={register({
                  required: "Password is required",
                  minLength: { value: 8, message: "minimum 8 characters" },
                })}
              />
              {errors.password && <p>{errors.password.message}</p>}
            </div>
            <input
              name="role_id"
              type="hidden"
              value={roleId}
              contentEditable={false}
              ref={register}
            />
            <input
              name="max_workshops"
              type="hidden"
              value="3"
              contentEditable={false}
              ref={register}
            />
            <input
              name="registration_date"
              type="hidden"
              value={now_formated}
              contentEditable={false}
              ref={register}
            />

            <button type="submit">Register Account</button>
            <hr />
          </form>
          <p>
            already have an account? <Link to="/login">login</Link>
          </p>
          <p>Forgot your account?</p>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
